def hackerrankInString(s):
    match = "hackerrank"
    next_index = 0
    # loop
    for el in s:
        # if el matches the value of match at next_index
        if el == match[next_index]:
            # increment next_index
            next_index += 1
        # if next_index is equal to length of match, means all elements were matched
        if next_index == len(match):
            return "YES"
    # if it completes loop, match wasn't successful
    return "NO"

